import React, { createContext, useEffect, useReducer } from 'react';
import items from '../data';

import RoomReducer from '../Reducer/RootReducer';

const RoomContext = createContext();

const RootContext = ({ children }) => {
  const [data, dispatch] = useReducer(RoomReducer, {
    rooms: [],
    sortedRooms: [],
    featuredRooms: [],
    loading: true,
  });

  useEffect(() => {
    console.log(data.rooms);
    dispatch({ type: 'INIT', rooms: formatData(items) });
  }, []);

  const formatData = (items) => {
    let tempItems = items.map((item) => {
      let id = item.sys.id;
      let images = item.fields.images.map((image) => image.fields.file.url);
      let room = { ...item.fields, id, images };
      return room;
    });
    return tempItems;
  };

  return (
    <RoomContext.Provider value={{ ...data }}>{children}</RoomContext.Provider>
  );
};

export { RootContext, RoomContext };
