import React, { createContext } from 'react';
import data from '../data';

const themes = {
  light: {
    foreground: '#000000',
    background: '#eeeeee',
  },
  dark: {
    foreground: '#ffffff',
    background: '#222222',
  },
};

const RoomContext = createContext();

const RootContext = ({ children }) => {
  return (
    <RoomContext.Provider value={{ ...themes }}>
      {children}
    </RoomContext.Provider>
  );
};

export { RootContext, RoomContext };
