import React, { useContext, useEffect, useState } from 'react';
import { RoomContext } from '../context';
import { Link } from 'react-router-dom';

import Banner from '../Banner';
import StyledHero from '../styles/StyleHero';

const SingleRoom = ({ match }) => {
  const { getRoom } = useContext(RoomContext);
  const [data, setData] = useState(null);

  useEffect(() => {
    const room = getRoom(match.params.slug);
    setData(room);
  }, [match, data, getRoom]);

  if (!data) {
    return (
      <div className='error'>
        <h3>no such room could be found</h3>
        <Link to='/rooms' className='btn-primary'>
          back to rooms
        </Link>
      </div>
    );
  }

  return (
    <>
      <StyledHero img={data.images[0]}>
        <Banner title={`${data.name} room`}>
          <Link to='/rooms' className='btn-primary'>
            back to rooms
          </Link>
        </Banner>
      </StyledHero>

      <section className='single-room'>
        <div className='single-room-images'>
          {data.images.slice(1).map((item, index) => (
            <img src={item} alt='img' key={index} />
          ))}
        </div>
        <div className='single-room-info'>
          <article className='desc'>
            <h3>details</h3>
            <p>{data.description}</p>
          </article>
          <article className='info'>
            <h3>info</h3>
            <h6>price: ${data.price}</h6>
            <h6>size: {data.size} SQFT</h6>
            <h6>Max Capacity: {data.size} SQFT</h6>
            <h6>
              max capacity :{' '}
              {data.capacity > 1
                ? `${data.capacity} people`
                : `${data.capacity} person`}
            </h6>
            <h6>{data.pets ? `pets allowed` : `no pets allowed`}</h6>
          </article>
        </div>
      </section>
    </>
  );
};

export default SingleRoom;
