import React, { createContext, useEffect, useState } from 'react';
import items from '../data';

const RoomContext = createContext();

const RootContext = ({ children }) => {
  const [data, setData] = useState({
    rooms: [],
    sortedRooms: [],
    featuredRooms: [],
    loading: true,
  });

  const [format, setFormat] = useState(data);

  // const { rooms2, sortedRooms, featuredRooms, loading } = data;

  useEffect(() => {
    const async = async () => {
      let forMatRooms = await formatData(items);
      setFormat((prevState) => {
        return { ...prevState, rooms: forMatRooms };
      });
    };
    async();
  }, [data]);

  const formatData = (items) => {
    let tempItems = items.map((item) => {
      let id = item.sys.id;
      let images = item.fields.images.map((image) => image.fields.file.url);
      let room = { ...item.fields, id, images };
      return room;
    });
    return tempItems;
  };

  return (
    <RoomContext.Provider value={{ ...data }}>{children}</RoomContext.Provider>
  );
};

export { RootContext, RoomContext };
