      },
      {
        icon: <FaBeer />,
        title: 'Strongest Beer',
        info:
          'Lorem ipsum dolor sit amet consectetur adipisicing elit. Iusto asperiores incidunt aperiam earum quibusdam veritatis harum sequi. Asperiores, adipisci enim?',
      },
    ],
  });

  return (
    <section className='services'>
      <Title title='services' />
      <div className='services-center'>
        {Services.services.map(services, (i) => {
          return (
            <article key={i} className='service'>
              <span>{services.icon}</span>
              <h6>{services.title}</h6>
              <p>{services.info}</p>
            </article>
          );
        })}
      </div>
    </section>
  );
};

export default Services;
