import React, { useState, useEffect, useCallback } from 'react';
import items from '../data';

const RoomContext = React.createContext();

const RoomProvider = ({ children }) => {
  // const [Rooms, setRooms] = useState([]);
  // const [sortedRooms, setSortedRooms] = useState([]);
  // const [featuredRooms, setFeaturedRooms] = useState([]);
  // const [loading, setloading] = useState([]);
  const [update, setUpdate] = useState({
    rooms: [],
    sorted: [],
    featured: [],
    loading: true,
  });

  useCallback(() => {
    let rooms = formatData(items);
    let featured = roomsData.filter((room) => room.feature === true);

    setUpdate({ rooms, featured, sorted: rooms, loading: false });
  }, [update]);

  const formatData = (items) => {
    let tempItems = items.map((item) => {
      let id = item.sys.id;
      let img = item.fields.images.map((image) => image.fields.file.url);
      let room = { ...item.fields, img, id };

      return room;
    });
    return tempItems;
  };

  return (
    <RoomContext.Provider value={'ㅇㅁㅇ'}>{children}</RoomContext.Provider>
  );
};

const RoomConsumer = RoomContext.Consumer;

export { RoomProvider, RoomConsumer, RoomContext };
